import gspread
import json
import base64
from oauth2client.service_account import ServiceAccountCredentials

# Your Base64 string
base64_string = ''


# Fix padding
missing_padding = len(base64_string) % 4
if missing_padding:
    base64_string += '=' * (4 - missing_padding)

# Decoding the key and adding logging
print("Starting to decode Base64 string")
credentials_json = base64.b64decode(base64_string).decode('utf-8')
print("Decoded Base64 string")

print("Loading credentials")
creds_dict = json.loads(credentials_json)

print("Authorizing with Google API")
creds = ServiceAccountCredentials.from_json_keyfile_dict(creds_dict, ["https://spreadsheets.google.com/feeds", "https://www.googleapis.com/auth/drive"])
client = gspread.authorize(creds)

print("Opening spreadsheet")
spreadsheet = client.open_by_url("https://docs.google.com/spreadsheets/d/1-JBOTTfWizQnY4-JFXVPhCbFuWkcqutNMgd-L_NoFak")

print("Accessing first sheet")
sheet = spreadsheet.sheet1

print("Getting first row, first 5 columns")
first_row = sheet.row_values(1)[:5]

if not first_row:
    print("The first row is empty or there was an issue retrieving it.")
else:
    print("First row values:", first_row)